mapper.select:
select s.*,c.name categoryName from setmeal s left join category c on s.category_id = c.id

    and s.name like concat('%',#{name},'%')
    and s.category_id=#{categoryId}
    and s.status=#{status}

order by create_time

mapper.update:
update setmeal
category_id=#{categoryId}, description=#{description}, image=#{image}, name=#{name}, price=#{price}, status=#{status},
where id = #{id}
